    }
    // Comparar la contraseÃ±a 
    const passOK = await compare(password, user.password);
    if (passOK === true) {
        const token = sign(
            {
                usuario: user.documento,
                rol: user.perfil,
                nombre: user.nombre
            },
            process.env.JWT_SECRET_KEY
        )
        return res.status(200).json({ estado: "ok", msg: "Logueado", token, url:"/inicio" });
